package com.bridgelabz.demo;

public class computequotient {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
  int a = 525;
  int b = 5;
  
  int quotient = a/b;
  int reminder = a%b;
  
  System.out.println("Quotient is " + quotient);
  System.out.println("Reminder is "+  reminder);
	}

}
